/**
 * Language Selector Component
 * Beautiful multilingual dropdown with Indian language support
 */

import React, { useState, useRef, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { languages } from '../i18n/i18n';

const LanguageSelector = () => {
  const { i18n, t } = useTranslation();
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  const currentLanguage = languages.find(lang => lang.code === i18n.language) || languages[0];

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleLanguageChange = (languageCode) => {
    i18n.changeLanguage(languageCode);
    setIsOpen(false);
  };

  return (
    <div className="relative z-[9998]" ref={dropdownRef}>
      <button
        className="flex items-center space-x-3 px-4 py-2 rounded-xl bg-white/10 backdrop-blur-lg border border-white/20 text-white font-medium hover:bg-white/20 hover:scale-105 hover:border-blue-400/50 transition-all duration-300 min-w-[140px] shadow-lg relative z-[9999]"
        onClick={() => setIsOpen(!isOpen)}
        aria-label={t('header.language')}
      >
        <span className="text-xl">{currentLanguage.flag}</span>
        <span className="font-medium text-white">{currentLanguage.nativeName}</span>
        <svg
          className={`w-4 h-4 text-gray-300 transition-transform duration-200 ${
            isOpen ? 'rotate-180' : 'rotate-0'
          }`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
        </svg>
      </button>

      {isOpen && (
        <div className="absolute top-full left-0 mt-2 w-56 bg-gray-900/95 backdrop-blur-xl border border-white/20 rounded-xl shadow-2xl z-[9999] animate-fade-in-up overflow-hidden">
          <div className="p-2">
            <div className="text-xs font-semibold text-gray-400 uppercase tracking-wider px-3 py-2 border-b border-gray-700 mb-2">
              {t('header.language')}
            </div>
            {languages.map((language) => (
              <button
                key={language.code}
                className={`w-full flex items-center space-x-3 px-3 py-3 rounded-lg text-left transition-all duration-200 hover:bg-white/10 relative z-[10000] ${
                  currentLanguage.code === language.code
                    ? 'bg-blue-500/20 border border-blue-500/30 text-white'
                    : 'text-gray-100 hover:text-white hover:scale-102'
                }`}
                onClick={() => handleLanguageChange(language.code)}
              >
                <span className="text-2xl">{language.flag}</span>
                <div className="flex-1">
                  <div className="font-medium">{language.nativeName}</div>
                  <div className="text-xs text-gray-400">{language.name}</div>
                </div>
                {currentLanguage.code === language.code && (
                  <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                )}
              </button>
            ))}
          </div>
          
          {/* Language Stats */}
          <div className="border-t border-gray-700 p-3 bg-gray-800/50">
            <div className="text-xs text-gray-400 text-center">
              <span className="inline-flex items-center">
                <span className="text-blue-400 mr-1">üåç</span>
                {languages.length} languages supported
              </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LanguageSelector;
