/**
 * Live Monitoring Component
 * Real-time display of sensor data and risk predictions
 */

import React, { useState, useEffect, useRef } from 'react';
import { Line, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { rockfallAPI, formatRiskLevel } from '../services/api';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const LiveMonitoring = () => {
  const [currentData, setCurrentData] = useState(null);
  const [historicalData, setHistoricalData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const intervalRef = useRef(null);

  // Fetch live data from API
  const fetchLiveData = async () => {
    try {
      const data = await rockfallAPI.getMockData();
      setCurrentData(data);
      setLastUpdate(new Date());
      
      // Add to historical data (keep last 20 points)
      setHistoricalData(prev => {
        const newData = [...prev, {
          timestamp: new Date().toLocaleTimeString(),
          risk: data.prediction.risk_probability,
          category: data.prediction.risk_category,
          vibration: data.sensor_data.vibration_intensity,
          rainfall: data.sensor_data.rainfall_24h,
          slope: data.sensor_data.slope_angle,
        }];
        return newData.slice(-20);
      });
      
      setError(null);
    } catch (err) {
      console.error('Failed to fetch live data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Initial fetch
    fetchLiveData();
    
    // Set up polling interval (every 3 seconds)
    intervalRef.current = setInterval(fetchLiveData, 3000);
    
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  // Chart configurations
  const riskTrendChart = {
    data: {
      labels: historicalData.map(d => d.timestamp),
      datasets: [{
        label: 'Risk Probability (%)',
        data: historicalData.map(d => d.risk),
        borderColor: 'rgb(59, 130, 246)',
        backgroundColor: 'rgba(59, 130, 246, 0.1)',
        tension: 0.4,
        fill: true,
      }]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        title: {
          display: true,
          text: 'Risk Probability Trend (Live)',
        },
        legend: {
          display: false,
        },
      },
      scales: {
        y: {
          beginAtZero: true,
          max: 100,
          ticks: {
            callback: (value) => `${value}%`
          }
        },
        x: {
          display: false, // Hide x-axis labels for cleaner look
        }
      },
      interaction: {
        intersect: false,
        mode: 'index',
      },
    }
  };

  const riskDistributionChart = {
    data: {
      labels: ['Low Risk', 'Medium Risk', 'High Risk', 'Critical Risk'],
      datasets: [{
        data: currentData ? [
          currentData.prediction.category_probabilities.Low || 0,
          currentData.prediction.category_probabilities.Medium || 0,
          currentData.prediction.category_probabilities.High || 0,
          currentData.prediction.category_probabilities.Critical || 0,
        ] : [25, 25, 25, 25],
        backgroundColor: [
          '#10b981',
          '#f59e0b', 
          '#ef4444',
          '#dc2626',
        ],
        borderColor: '#fff',
        borderWidth: 2,
      }]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        title: {
          display: true,
          text: 'Current Risk Distribution',
        },
        legend: {
          position: 'bottom',
        },
      },
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="spinner"></div>
        <p className="ml-4 text-gray-600">Loading live monitoring data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <div className="flex items-center">
          <div className="text-red-500 mr-3">⚠️</div>
          <div>
            <h3 className="text-red-800 font-medium">Failed to Load Live Data</h3>
            <p className="text-red-600 text-sm mt-1">{error}</p>
            <button 
              onClick={fetchLiveData}
              className="mt-3 btn btn-danger text-sm"
            >
              Retry Connection
            </button>
          </div>
        </div>
      </div>
    );
  }

  const currentRisk = currentData?.prediction;
  const sensorData = currentData?.sensor_data;
  const systemStatus = currentData?.system_status;
  const riskStyle = formatRiskLevel(currentRisk?.risk_category);

  return (
    <div className="space-y-6 slide-in">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Live Monitoring</h2>
          <p className="text-gray-600">Real-time rockfall risk assessment</p>
        </div>
        <div className="text-sm text-gray-500">
          Last updated: {lastUpdate.toLocaleTimeString()}
        </div>
      </div>

      {/* Current Risk Status */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className={`card p-6 border-l-4`} style={{ borderLeftColor: riskStyle.color, backgroundColor: riskStyle.bgColor }}>
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Current Risk Level</p>
              <p className="text-3xl font-bold mt-2" style={{ color: riskStyle.color }}>
                {currentRisk?.risk_category || 'Unknown'}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                {currentRisk?.risk_probability || 0}% probability
              </p>
            </div>
            <div className="text-4xl">
              {currentRisk?.risk_category === 'Low' && '🟢'}
              {currentRisk?.risk_category === 'Medium' && '🟡'}
              {currentRisk?.risk_category === 'High' && '🟠'}
              {currentRisk?.risk_category === 'Critical' && '🔴'}
            </div>
          </div>
        </div>

        <div className="card p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Sensor Status</p>
              <p className={`text-xl font-bold mt-2 ${systemStatus?.sensors_online ? 'text-green-600' : 'text-red-600'}`}>
                {systemStatus?.sensors_online ? 'Online' : 'Offline'}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                {sensorData?.sensor_id || 'Unknown Sensor'}
              </p>
            </div>
            <div className={`text-3xl ${systemStatus?.sensors_online ? 'text-green-500' : 'text-red-500'}`}>
              {systemStatus?.sensors_online ? '📡' : '📵'}
            </div>
          </div>
        </div>

        <div className="card p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Prediction Confidence</p>
              <p className="text-xl font-bold mt-2 text-blue-600">
                {currentRisk?.confidence || 0}%
              </p>
              <p className="text-sm text-gray-600 mt-1">
                Model accuracy
              </p>
            </div>
            <div className="text-3xl">🎯</div>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="card p-6">
          <div className="chart-container chart-medium">
            <Line data={riskTrendChart.data} options={riskTrendChart.options} />
          </div>
        </div>

        <div className="card p-6">
          <div className="chart-container chart-medium">
            <Doughnut data={riskDistributionChart.data} options={riskDistributionChart.options} />
          </div>
        </div>
      </div>

      {/* Sensor Readings */}
      <div className="card p-6">
        <h3 className="text-lg font-semibold mb-4">Current Sensor Readings</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {sensorData && Object.entries({
            'Slope Angle': `${sensorData.slope_angle}°`,
            'Rock Strength': `${sensorData.rock_strength} MPa`,
            'Joint Spacing': `${sensorData.joint_spacing}m`,
            'Rainfall (24h)': `${sensorData.rainfall_24h}mm`,
            'Vibration': `${sensorData.vibration_intensity} mm/s`,
            'Wind Speed': `${sensorData.wind_speed} m/s`,
          }).map(([label, value]) => (
            <div key={label} className="text-center p-3 bg-gray-50 rounded-lg">
              <p className="text-xs text-gray-600 uppercase tracking-wide">{label}</p>
              <p className="text-lg font-semibold text-gray-900 mt-1">{value}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Location Info */}
      <div className="card p-6">
        <h3 className="text-lg font-semibold mb-4">Monitoring Location</h3>
        <div className="flex items-center space-x-4">
          <div className="text-2xl">📍</div>
          <div>
            <p className="font-medium">{sensorData?.location || 'Zone-A-12'}</p>
            <p className="text-sm text-gray-600">
              Sensor: {sensorData?.sensor_id || 'MINE_SENSOR_1001'}
            </p>
            <p className="text-sm text-gray-600">
              Excavation Height: {sensorData?.excavation_height || 25}m
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LiveMonitoring;
