/**
 * Risk Maps & Alerts Component
 * Mock displays of risk visualization and alert systems
 */

import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const RiskMaps = () => {
  const [selectedZone, setSelectedZone] = useState('Zone-A');
  const [alertHistory, setAlertHistory] = useState([]);
  const [mockRiskData, setMockRiskData] = useState({});

  useEffect(() => {
    // Generate mock risk data for different zones
    const generateMockData = () => {
      const zones = ['Zone-A', 'Zone-B', 'Zone-C', 'Zone-D'];
      const riskData = {};
      
      zones.forEach(zone => {
        riskData[zone] = {
          currentRisk: Math.floor(Math.random() * 100),
          riskCategory: ['Low', 'Medium', 'High', 'Critical'][Math.floor(Math.random() * 4)],
          sensors: Math.floor(Math.random() * 8) + 2,
          lastAlert: new Date(Date.now() - Math.random() * 7 * 24 * 60 * 60 * 1000),
          elevation: Math.floor(Math.random() * 200) + 50,
          activeAlerts: Math.floor(Math.random() * 3),
        };
      });
      
      setMockRiskData(riskData);
    };

    // Generate mock alert history
    const generateAlertHistory = () => {
      const alerts = [];
      const alertTypes = ['High Risk Warning', 'Sensor Offline', 'Maintenance Required', 'Weather Alert'];
      const severities = ['Low', 'Medium', 'High', 'Critical'];
      
      for (let i = 0; i < 15; i++) {
        alerts.push({
          id: i + 1,
          type: alertTypes[Math.floor(Math.random() * alertTypes.length)],
          zone: `Zone-${String.fromCharCode(65 + Math.floor(Math.random() * 4))}`,
          severity: severities[Math.floor(Math.random() * severities.length)],
          timestamp: new Date(Date.now() - Math.random() * 7 * 24 * 60 * 60 * 1000),
          message: `Automated alert generated by monitoring system`,
          resolved: Math.random() > 0.3,
        });
      }
      
      setAlertHistory(alerts.sort((a, b) => b.timestamp - a.timestamp));
    };

    generateMockData();
    generateAlertHistory();
  }, []);

  const getSeverityColor = (severity) => {
    const colors = {
      Low: '#10b981',
      Medium: '#f59e0b',
      High: '#ef4444',
      Critical: '#dc2626',
    };
    return colors[severity] || '#6b7280';
  };

  const zoneRiskChart = {
    data: {
      labels: Object.keys(mockRiskData),
      datasets: [
        {
          label: 'Risk Level (%)',
          data: Object.values(mockRiskData).map(zone => zone.currentRisk || 0),
          backgroundColor: Object.values(mockRiskData).map(zone => {
            const risk = zone.currentRisk || 0;
            if (risk < 25) return 'linear-gradient(135deg, #10b981, #34d399)';
            if (risk < 50) return 'linear-gradient(135deg, #f59e0b, #fbbf24)';
            if (risk < 75) return 'linear-gradient(135deg, #ef4444, #f87171)';
            return 'linear-gradient(135deg, #dc2626, #ef4444)';
          }),
          borderColor: 'rgba(255, 255, 255, 0.2)',
          borderWidth: 1,
          borderRadius: 8,
          hoverBackgroundColor: Object.values(mockRiskData).map(zone => {
            const risk = zone.currentRisk || 0;
            if (risk < 25) return '#34d399';
            if (risk < 50) return '#fbbf24';
            if (risk < 75) return '#f87171';
            return '#ef4444';
          }),
        }
      ]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        title: {
          display: true,
          text: 'Risk Levels by Mine Zone',
          color: '#e5e7eb',
          font: {
            size: 16,
            weight: 'bold'
          }
        },
        legend: {
          display: false,
        },
        tooltip: {
          backgroundColor: 'rgba(17, 24, 39, 0.95)',
          titleColor: '#f9fafb',
          bodyColor: '#d1d5db',
          borderColor: 'rgba(59, 130, 246, 0.5)',
          borderWidth: 1,
        }
      },
      scales: {
        x: {
          ticks: {
            color: '#9ca3af',
            font: {
              size: 12
            }
          },
          grid: {
            color: 'rgba(75, 85, 99, 0.2)',
          }
        },
        y: {
          beginAtZero: true,
          max: 100,
          ticks: {
            color: '#9ca3af',
            font: {
              size: 12
            },
            callback: (value) => `${value}%`
          },
          grid: {
            color: 'rgba(75, 85, 99, 0.2)',
          }
        }
      },
    }
  };

  return (
    <div className="space-y-8 slide-in">
      {/* Header */}
      <div className="text-center mb-8 fade-in-up">
        <div className="inline-flex items-center justify-center w-20 h-20 rounded-full bg-gradient-to-r from-red-500 to-orange-500 mb-6 pulse-glow">
          <span className="text-3xl">🗺️</span>
        </div>
        <h2 className="text-4xl font-bold bg-gradient-to-r from-red-400 via-orange-400 to-yellow-400 bg-clip-text text-transparent mb-4">
          Risk Maps & Alerts
        </h2>
        <p className="text-gray-300 text-lg max-w-2xl mx-auto">
          Advanced spatial risk visualization and intelligent alert management system
        </p>
      </div>

      {/* Zone Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {Object.entries(mockRiskData).map(([zone, data]) => (
          <div
            key={zone}
            className={`card p-4 cursor-pointer border-2 ${
              selectedZone === zone ? 'border-blue-500' : 'border-transparent'
            }`}
            onClick={() => setSelectedZone(zone)}
          >
            <div className="flex items-center justify-between mb-2">
              <h4 className="font-medium">{zone}</h4>
              <div
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: getSeverityColor(data.riskCategory) }}
              />
            </div>
            <div className="text-2xl font-bold mb-1" style={{ color: getSeverityColor(data.riskCategory) }}>
              {data.currentRisk}%
            </div>
            <div className="text-sm text-gray-600">
              <p>{data.sensors} sensors active</p>
              <p>Elevation: {data.elevation}m</p>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Risk Distribution Chart */}
        <div className="card p-6">
          <div className="chart-container chart-medium">
            <Bar data={zoneRiskChart.data} options={zoneRiskChart.options} />
          </div>
        </div>

        {/* Mock Risk Heatmap */}
        <div className="card p-6">
          <h3 className="text-lg font-semibold mb-4">Mine Site Risk Heatmap (Mock)</h3>
          <div className="relative">
            <div className="bg-gray-200 rounded-lg p-4 h-64 flex items-center justify-center">
              <div className="text-center">
                <div className="text-4xl mb-4">🗺️</div>
                <p className="text-gray-600 font-medium">Interactive Risk Heatmap</p>
                <p className="text-sm text-gray-500 mt-2">
                  In production: Real-time spatial visualization of risk zones
                  overlaid on mine topography with DEM integration
                </p>
              </div>
            </div>
            
            {/* Mock zone overlays */}
            <div className="absolute top-6 left-6">
              <div className="bg-red-500 opacity-75 rounded-full w-8 h-8 flex items-center justify-center text-white text-xs font-bold">
                C
              </div>
            </div>
            <div className="absolute top-16 right-8">
              <div className="bg-yellow-500 opacity-75 rounded-full w-6 h-6 flex items-center justify-center text-white text-xs font-bold">
                B
              </div>
            </div>
            <div className="absolute bottom-12 left-12">
              <div className="bg-green-500 opacity-75 rounded-full w-5 h-5 flex items-center justify-center text-white text-xs font-bold">
                A
              </div>
            </div>
          </div>
          
          <div className="mt-4 flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="flex items-center">
                <div className="w-3 h-3 bg-green-500 rounded mr-2"></div>
                <span className="text-sm">Low Risk</span>
              </div>
              <div className="flex items-center">
                <div className="w-3 h-3 bg-yellow-500 rounded mr-2"></div>
                <span className="text-sm">Medium Risk</span>
              </div>
              <div className="flex items-center">
                <div className="w-3 h-3 bg-red-500 rounded mr-2"></div>
                <span className="text-sm">High Risk</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Alert Management */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Active Alerts */}
        <div className="card p-6">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-semibold">Active Alerts</h3>
            <button className="btn btn-primary text-sm">
              📧 Send Test Alert
            </button>
          </div>
          
          <div className="space-y-3">
            {alertHistory
              .filter(alert => !alert.resolved)
              .slice(0, 5)
              .map(alert => (
                <div
                  key={alert.id}
                  className="flex items-center p-3 bg-gray-50 rounded-lg"
                >
                  <div
                    className="w-3 h-3 rounded-full mr-3"
                    style={{ backgroundColor: getSeverityColor(alert.severity) }}
                  />
                  <div className="flex-1">
                    <div className="flex items-center justify-between">
                      <p className="font-medium text-sm">{alert.type}</p>
                      <span className="text-xs text-gray-500">
                        {alert.timestamp.toLocaleTimeString()}
                      </span>
                    </div>
                    <p className="text-xs text-gray-600 mt-1">
                      {alert.zone} - {alert.message}
                    </p>
                  </div>
                  <button className="ml-2 text-gray-400 hover:text-gray-600">
                    ✕
                  </button>
                </div>
              ))}
            
            {alertHistory.filter(alert => !alert.resolved).length === 0 && (
              <div className="text-center py-8 text-gray-500">
                <div className="text-3xl mb-2">✅</div>
                <p>No active alerts</p>
              </div>
            )}
          </div>
        </div>

        {/* Communication System Mock */}
        <div className="card p-6">
          <h3 className="text-lg font-semibold mb-4">Alert Communication System</h3>
          
          <div className="space-y-4">
            <div className="p-4 bg-blue-50 rounded-lg">
              <div className="flex items-center mb-2">
                <span className="text-2xl mr-3">📱</span>
                <div>
                  <p className="font-medium">SMS Alerts</p>
                  <p className="text-sm text-gray-600">Emergency notifications to field personnel</p>
                </div>
              </div>
              <div className="text-xs text-gray-500 bg-white p-2 rounded border">
                <p><strong>Sample SMS:</strong></p>
                <p>🚨 HIGH RISK ALERT - Zone-B</p>
                <p>Risk: 78% | Time: 14:32</p>
                <p>Recommend immediate evacuation</p>
              </div>
            </div>

            <div className="p-4 bg-green-50 rounded-lg">
              <div className="flex items-center mb-2">
                <span className="text-2xl mr-3">📧</span>
                <div>
                  <p className="font-medium">Email Reports</p>
                  <p className="text-sm text-gray-600">Detailed reports to management</p>
                </div>
              </div>
              <div className="text-xs text-gray-500 bg-white p-2 rounded border">
                <p><strong>Sample Email Subject:</strong></p>
                <p>Daily Risk Assessment Report - OpenPit Mine Alpha</p>
                <p>3 zones monitored | 2 alerts resolved</p>
              </div>
            </div>

            <div className="p-4 bg-yellow-50 rounded-lg">
              <div className="flex items-center mb-2">
                <span className="text-2xl mr-3">🔊</span>
                <div>
                  <p className="font-medium">Site Alarms</p>
                  <p className="text-sm text-gray-600">Audio/visual warnings for immediate evacuation</p>
                </div>
              </div>
              <div className="text-xs text-gray-500">
                <p>• Siren activation for Critical alerts</p>
                <p>• LED warning lights by risk level</p>
                <p>• PA system announcements</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Alert History */}
      <div className="card p-6">
        <h3 className="text-lg font-semibold mb-4">Alert History</h3>
        
        <div className="overflow-x-auto">
          <table className="min-w-full">
            <thead>
              <tr className="border-b">
                <th className="text-left py-3 px-4 font-medium text-gray-700">Time</th>
                <th className="text-left py-3 px-4 font-medium text-gray-700">Zone</th>
                <th className="text-left py-3 px-4 font-medium text-gray-700">Alert Type</th>
                <th className="text-left py-3 px-4 font-medium text-gray-700">Severity</th>
                <th className="text-left py-3 px-4 font-medium text-gray-700">Status</th>
              </tr>
            </thead>
            <tbody>
              {alertHistory.slice(0, 10).map(alert => (
                <tr key={alert.id} className="border-b hover:bg-gray-50">
                  <td className="py-3 px-4 text-sm text-gray-600">
                    {alert.timestamp.toLocaleString()}
                  </td>
                  <td className="py-3 px-4 text-sm font-medium">
                    {alert.zone}
                  </td>
                  <td className="py-3 px-4 text-sm">
                    {alert.type}
                  </td>
                  <td className="py-3 px-4">
                    <span
                      className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium text-white"
                      style={{ backgroundColor: getSeverityColor(alert.severity) }}
                    >
                      {alert.severity}
                    </span>
                  </td>
                  <td className="py-3 px-4">
                    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      alert.resolved 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {alert.resolved ? 'Resolved' : 'Active'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Production Integration Note */}
      <div className="card p-6 bg-blue-50 border-blue-200">
        <div className="flex items-start">
          <div className="text-blue-500 mr-3 text-2xl">ℹ️</div>
          <div>
            <h4 className="font-medium text-blue-900 mb-2">Production Integration</h4>
            <p className="text-blue-800 text-sm mb-3">
              In a production system, this section would integrate with:
            </p>
            <ul className="text-blue-700 text-sm space-y-1">
              <li>• <strong>GIS Systems:</strong> Real mine topography and DEM data</li>
              <li>• <strong>Drone Imagery:</strong> Aerial surveillance and change detection</li>
              <li>• <strong>IoT Sensors:</strong> Real-time environmental and structural monitoring</li>
              <li>• <strong>Weather APIs:</strong> Live weather data integration</li>
              <li>• <strong>SMS/Email Services:</strong> Automated alert delivery systems</li>
              <li>• <strong>Site Communication:</strong> Integration with site PA and alarm systems</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RiskMaps;
