/**
 * Main Application Component
 * Rockfall Prediction Dashboard - Smart India Hackathon 2024
 */

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import LiveMonitoring from './components/LiveMonitoring';
import PredictionDemo from './components/PredictionDemo';
import RiskMaps from './components/RiskMaps';
import SystemOverview from './components/SystemOverview';
import { rockfallAPI } from './services/api';

function App() {
  const [apiStatus, setApiStatus] = useState({ online: false, loading: true });
  const [currentTime, setCurrentTime] = useState(new Date());

  // Check API status on component mount
  useEffect(() => {
    const checkApiStatus = async () => {
      try {
        await rockfallAPI.checkHealth();
        setApiStatus({ online: true, loading: false });
      } catch (error) {
        console.error('API Health Check Failed:', error);
        setApiStatus({ online: false, loading: false });
      }
    };

    checkApiStatus();
    
    // Update time every second
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    // Check API status every 30 seconds
    const healthInterval = setInterval(checkApiStatus, 30000);

    return () => {
      clearInterval(timeInterval);
      clearInterval(healthInterval);
    };
  }, []);

  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <header className="bg-white shadow-sm border-b">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              {/* Logo and Title */}
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <h1 className="text-xl font-bold text-gray-900">
                    🏔️ Rockfall Prediction System
                  </h1>
                  <p className="text-sm text-gray-500">
                    Smart India Hackathon 2024 | Problem ID: 25071
                  </p>
                </div>
              </div>

              {/* Status and Time */}
              <div className="flex items-center space-x-6">
                <div className="text-sm text-gray-600">
                  {currentTime.toLocaleString()}
                </div>
                
                <div className="flex items-center space-x-2">
                  <div
                    className={`w-3 h-3 rounded-full ${
                      apiStatus.loading
                        ? 'bg-yellow-400 pulse-dot'
                        : apiStatus.online
                        ? 'bg-green-400'
                        : 'bg-red-400'
                    }`}
                  />
                  <span className="text-sm font-medium">
                    {apiStatus.loading
                      ? 'Connecting...'
                      : apiStatus.online
                      ? 'System Online'
                      : 'System Offline'
                    }
                  </span>
                </div>
              </div>
            </div>
          </div>
        </header>

        {/* Navigation */}
        <nav className="bg-white shadow-sm">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex space-x-8">
              <NavLink
                to="/"
                className={({ isActive }) =>
                  `inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                    isActive
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  } transition-colors duration-200`
                }
              >
                📊 Live Monitoring
              </NavLink>

              <NavLink
                to="/prediction"
                className={({ isActive }) =>
                  `inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                    isActive
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  } transition-colors duration-200`
                }
              >
                🤖 Prediction Demo
              </NavLink>

              <NavLink
                to="/risk-maps"
                className={({ isActive }) =>
                  `inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                    isActive
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  } transition-colors duration-200`
                }
              >
                🗺️ Risk Maps & Alerts
              </NavLink>

              <NavLink
                to="/system-overview"
                className={({ isActive }) =>
                  `inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                    isActive
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  } transition-colors duration-200`
                }
              >
                ⚙️ System Overview
              </NavLink>
            </div>
          </div>
        </nav>

        {/* Main Content */}
        <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          {!apiStatus.online && !apiStatus.loading && (
            <div className="mb-6 p-4 bg-red-50 border-l-4 border-red-400 text-red-700 rounded-md">
              <div className="flex">
                <div className="flex-shrink-0">
                  ⚠️
                </div>
                <div className="ml-3">
                  <p className="font-medium">API Connection Failed</p>
                  <p className="text-sm mt-1">
                    Unable to connect to the backend API. Please ensure the Flask server is running on{' '}
                    <code className="bg-red-100 px-1 rounded">http://localhost:5000</code>
                  </p>
                </div>
              </div>
            </div>
          )}

          <Routes>
            <Route path="/" element={<LiveMonitoring />} />
            <Route path="/prediction" element={<PredictionDemo />} />
            <Route path="/risk-maps" element={<RiskMaps />} />
            <Route path="/system-overview" element={<SystemOverview />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer className="bg-gray-800 text-white mt-12">
          <div className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              <div>
                <h3 className="text-lg font-semibold mb-4">Project Details</h3>
                <p className="text-gray-300 text-sm">
                  AI-Based Rockfall Prediction and Alert System for Open-Pit Mines
                </p>
                <p className="text-gray-400 text-xs mt-2">
                  Problem ID: 25071 - Ministry of Mines / NIRM
                </p>
              </div>
              
              <div>
                <h3 className="text-lg font-semibold mb-4">Technology Stack</h3>
                <ul className="text-gray-300 text-sm space-y-1">
                  <li>• Flask API + ML Models</li>
                  <li>• React.js + Chart.js</li>
                  <li>• Scikit-learn + NumPy</li>
                  <li>• Real-time Data Processing</li>
                </ul>
              </div>
              
              <div>
                <h3 className="text-lg font-semibold mb-4">Deployment</h3>
                <ul className="text-gray-300 text-sm space-y-1">
                  <li>• Backend: Render / Appwrite</li>
                  <li>• Frontend: Vercel / Netlify</li>
                  <li>• Cloud-ready Architecture</li>
                  <li>• Environment Configuration</li>
                </ul>
              </div>
            </div>
            
